mapper.select:
	SELECT 
      id_app_proceso_log , 
      fecha_inicio_ejecucion , 
      fecha_fin_ejecucion , 
      estado_ejecucion , 
      descripcion_estado_ejecucion , 
      clase_programacion , 
      metodo_programacion , 
      parametro_entrada , 
      resultado_salida , 
      usuario , 
      duracion_ms 
	FROM
	pp_app_proceso_log
	WHERE 1 = 1

		AND id_app_proceso_log = #{id, jdbcType=INTEGER}


		AND fecha_inicio_ejecucion = #{fechaInicioEjecucion, jdbcType=TIMESTAMP}


		AND fecha_fin_ejecucion = #{fechaFinEjecucion, jdbcType=TIMESTAMP}


		AND estado_ejecucion = #{estadoEjecucion}


		AND descripcion_estado_ejecucion = #{descripcionEstadoEjecucion}


		AND clase_programacion = #{claseProgramacion}


		AND metodo_programacion = #{metodoProgramacion}


		AND parametro_entrada = #{parametroEntrada}


		AND resultado_salida = #{resultadoSalida}


		AND usuario = #{usuario}


		AND duracion_ms = #{duracionMs, jdbcType=INTEGER}

mapper.insert:
INSERT INTO pp_app_proceso_log( id_app_proceso_log , fecha_inicio_ejecucion , fecha_fin_ejecucion , estado_ejecucion , descripcion_estado_ejecucion , clase_programacion , metodo_programacion , parametro_entrada , resultado_salida , usuario , duracion_ms ) (
SELECT #{element.id} , #{element.fechaInicioEjecucion} , #{element.fechaFinEjecucion} , #{element.estadoEjecucion} , #{element.descripcionEstadoEjecucion} , #{element.claseProgramacion} , #{element.metodoProgramacion} , #{element.parametroEntrada} , #{element.resultadoSalida} , #{element.usuario} , #{element.duracionMs} from dummy
)

mapper.delete:
    DELETE FROM pp_app_proceso_log
	WHERE 1 = 1

		AND id_app_proceso_log = #{id, jdbcType=INTEGER}


		AND fecha_inicio_ejecucion = #{fechaInicioEjecucion, jdbcType=TIMESTAMP}


		AND fecha_fin_ejecucion = #{fechaFinEjecucion, jdbcType=TIMESTAMP}


		AND estado_ejecucion = #{estadoEjecucion}


		AND descripcion_estado_ejecucion = #{descripcionEstadoEjecucion}


		AND clase_programacion = #{claseProgramacion}


		AND metodo_programacion = #{metodoProgramacion}


		AND parametro_entrada = #{parametroEntrada}


		AND resultado_salida = #{resultadoSalida}


		AND usuario = #{usuario}


		AND duracion_ms = #{duracionMs, jdbcType=INTEGER}
